import React, { useState } from "react";

import "./ExpenseFilter.css";

const ExpensesFilter = (props) => {
  const [userSelectedValue, setUserSelectedValue] = useState(2022);
  const handleUserSelected = (e) => {
    setUserSelectedValue(e.target.value);
  };
  props.onFilterChange(userSelectedValue);
  console.log(userSelectedValue);
  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select onChange={handleUserSelected} value={userSelectedValue}>
          <option value="2022">2022</option>
          <option value="2021">2021</option>
          <option value="2020">2020</option>
          <option value="2019">2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
